import React from "react";
import { useState } from "react";

const Friday14july = () => {
  const [count, setCount] = useState(0);
  const [text, setText] = useState("");
  const [ison, setIson] = useState(false);
  const [todos, setTodos] = useState([]);

  const add = () => {
    setCount(count + 1);
  };
  const minus = () => {
    setCount(count - 1);
  };

  const handleonchange = (e) => {
    setText(e.target.value);
  };

  const handleonTogle = () => {
    setIson(!ison);
  };

  const handleAddTodo = () => {
    setTodos([...todos, "Add New Todo"]);
  };
  return (
    <>
      <button onClick={add} className="btn btn-primary">
        Add
      </button>
      <div>{count}</div>
      <button onClick={minus} className="btn btn-primary">
        Minus
      </button>
      <div>
        <input type="text" value={text} onChange={handleonchange} />
        <p>{text}</p>
      </div>
      <div>
        <button className="btn btn-primary" onClick={handleonTogle}>
          {ison ? "On" : "Off"}
        </button>
      </div>
      <div>
        <button className="btn btn-btn-primary" onClick={handleAddTodo}>
          Add Todo
        </button>
      </div>
    </>
  );
};

export default Friday14july;
